package condition;

public class elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=10,y=20;
		if(x<y)
		{
			
			System.out.println("x is greater then y");
		}
		else if(x==y)
		{
			System.out.println("X is equal to Y");
			
		}
		else if (x>y)
		{
			System.out.println("y is greater then x");
		}
	}

}
